import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

//importing jsx file to write inside html code
import Heading from './Heading';
import PIE , {doublepi,triplepi} from "./pi";

const root = ReactDOM.createRoot(document.getElementById('root'));

//styling
const img="https://picsum.photos/200";

//timing problem
const date= new Date(2023,19,2,16);  //year,month,day,hour
const currenttime = date.getHours();
let greeting;

if(currenttime < 12){
  greeting="good morning"
}
else if(currenttime < 18){
  greeting = "good afternoon"
}
else{
  greeting = "good night"
}


root.render( 
  <div>
    <h1 className='header'>Hello world</h1>
    <Heading/>
    <ul>  
      <li style={{color: "red"}}>Home</li>
      <li>About and {PIE}</li>
      <li>Services and {doublepi()}</li>
      <li>contact us and {triplepi()}</li>
      <br></br>
      <img src={img + "?grayscale"} />
    </ul>

    <h1>{greeting}</h1>
  </div>  
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
