import React ,{useState} from "react";

function Note(props){
    const [note,setnote]=useState({
        title:"",
        content:""
    });
    const [item,setitem]=useState([])

    function updatenote(event){
        const {name,value}=event.target;
        setnote(prevalue=>{
            return{
                ...prevalue,
                [name]:value
            }
        })
    }

    function additem(event){
       props.addnote();
       event.preventDefault();
    }

 return (
    <section>
        <div className="title"> 
            <input style={{fontWeight : "bold"}} name="title"   type="text" placeholder="Title" onChange={updatenote} value={note.title} />
            <input name="content"  type="text" placeholder="Take a Note" onChange={updatenote} value={note.content} />
            <br></br>
            <button onClick={additem}>Add</button>
        </div>
            <div className="note">
            <h3>this is note title</h3>
            <p>this is note content</p>
        </div>
 </section>
 );
}
export default Note;
